import React, { useState, useRef } from "react";

interface LogFileAddedResponse {
  message: string;
  logFileAdded: boolean;
}

interface AddLogFileRequest {
  logFileName: string;
  logFilePath: string;
  created: string;
  changed: string;
}

export const MainPage = () => {
  const [logFileName, setLogFileName] = useState("");
  const [logFilePath, setLogFilePath] = useState("");
  const [logFileResponse, setLogFileResponse] = useState<LogFileAddedResponse>({
    message: "",
    logFileAdded: false,
  });
  const [addLogFileRequest, setAddLogFileRequest] = useState<AddLogFileRequest>(
    { logFileName: "", logFilePath: "", created: "", changed: "" }
  );

  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleLogFileName = (e: React.ChangeEvent<HTMLInputElement>) => {
    setLogFileName(e.target.value);
  };

  const handleLogFilePath = (e: React.ChangeEvent<HTMLInputElement>) => {
    setLogFilePath(e.target.value);
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setLogFileName(file.name);
      setLogFilePath(file.name); // We can't get the full path, so we use the file name
    }
  };

  const triggerFileInput = () => {
    fileInputRef.current?.click();
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setAddLogFileResponse({
      logFileName: logFileName,
      logFilePath: logFilePath,
    });

    try {
      const response = await fetch("/logFile/addLogFile", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          addLogFileResponse,
        }),
      });

      const data: LogFileAddedResponse = await response.json();
      setLogFileResponse(data);
    } catch (error) {
      console.error("Error fetching log file:", error);
      setLogFileResponse({
        message: "Error adding log file. Please try again.",
        logFileAdded: false,
      });
    }
  };

  return (
    <div>
      <h1>Add Log File</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="logFileName">Log File Name:</label>
          <input
            className="form-control"
            type="text"
            id="logFileName"
            value={logFileName}
            onChange={handleLogFileName}
            required
          />
        </div>
        <div>
          <label htmlFor="logFilePath">Log File Path:</label>
          <div className="input-group">
            <input
              className="form-control"
              type="text"
              id="logFilePath"
              value={logFilePath}
              onChange={handleLogFilePath}
              required
            />
            <button
              type="button"
              className="btn btn-secondary"
              onClick={triggerFileInput}
            >
              Select File
            </button>
          </div>
          <input
            type="file"
            ref={fileInputRef}
            style={{ display: "none" }}
            onChange={handleFileSelect}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Add Log File
        </button>
      </form>
      {logFileResponse.message && <p>{logFileResponse.message}</p>}
    </div>
  );
};
